func TestFindAll1(t *testing.T) {
	text := "I like nike"
	trie := New("i", "like", "e ni")
	trie.SetSkip(NewSkipSpecialSymbols())
	trie.AddFilters(NewSingleEnglishWordFilter())
	emits := trie.FindAll(text)
	EqualEmit(t, emits[0], 0, 1, "I")
	EqualEmit(t, emits[1], 2, 6, "like")
	EqualEmit(t, emits[2], 5, 9, "e ni")
	t.Log(emits)
}

